CREATE OR REPLACE PROCEDURE atualizar_solic_aferese_terap ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
qt_peso_w		real;
qt_altura_cm_w		real;
qt_hemoglobina_w	double precision;
qt_hematocrito_w	double precision;
qt_plaqueta_w		bigint;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

	select	max(qt_peso),
		max(qt_altura_cm),
		max(qt_hemoglobina),
		max(qt_hematocrito),
		max(qt_plaqueta)
	into STRICT	qt_peso_w,
		qt_altura_cm_w,
		qt_hemoglobina_w,
		qt_hematocrito_w,
		qt_plaqueta_w
	from	prescr_solic_bco_sangue
	where	nr_prescricao = nr_prescricao_p;

	select	nextval('san_solic_aferese_terap_seq')
	into STRICT	nr_sequencia_w
	;

	insert into san_solic_aferese_terap(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_prescricao,
			nr_seq_prescr,
			dt_solicitacao,
			ie_status,
			cd_estabelecimento)
		values (
			nr_sequencia_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_prescricao_p,
			nr_seq_prescr_p,
			clock_timestamp(),
			'SM',
			cd_estabelecimento_p);

	insert into san_registro_aferese(
			nr_sequencia,
			nr_seq_aferese,
			dt_inicio,
			nm_usuario_ini,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			qt_peso,
			qt_altura_cm,
			qt_hemoglobina,
			pr_hematocrito,
			qt_plaqueta)
		values (
			nextval('san_registro_aferese_seq'),
			nr_sequencia_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			qt_peso_w,
			qt_altura_cm_w,
			qt_hemoglobina_w,
			qt_hematocrito_w,
			qt_plaqueta_w);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
