CREATE OR REPLACE PROCEDURE atualizar_split_pane_corsisf1 ( ie_fixar_p text, nr_posicao_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


qt_registros_w	bigint;


BEGIN

	select 	count(*)
	into STRICT	qt_registros_w
	from	split_pane_corsisf1
	where	nm_usuario = nm_usuario_p
	and		cd_estabelecimento = cd_estabelecimento_p;


	if (qt_registros_w > 0) then

		update  split_pane_corsisf1
		set     ie_fixar = ie_fixar_p,
			    nr_posicao = nr_posicao_p,
			    dt_atualizacao = clock_timestamp()
		where	nm_usuario = nm_usuario_p
		and		cd_estabelecimento = cd_estabelecimento_p;

	else
		insert into split_pane_corsisf1(nr_sequencia,
			cd_estabelecimento,
			nr_posicao,
		 	ie_fixar,
	 	 	dt_atualizacao,
	 	 	nm_usuario,
	 	 	dt_atualizacao_nrec,
	 	 	nm_usuario_nrec)
		values (nextval('split_pane_corsisf1_seq'),
			cd_estabelecimento_p,
			nr_posicao_p,
			ie_fixar_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p);
	end if;

	commit;

end;
$body$
LANGUAGE PLPGSQL
