CREATE OR REPLACE PROCEDURE atualizar_status_agenda_ge ( nr_sequencia_p bigint, nr_atendimento_p bigint, ie_atualizar_p text, nm_usuario_p text) AS $body$
BEGIN

if (ie_atualizar_p	= 'A') then

	update   agenda_consulta
	set      ie_status_agenda  =  'E'
        where    nr_sequencia      = nr_sequencia_p;

	commit;

	update   agenda_paciente
	set      nr_atendimento    = nr_atendimento_p,
		 ie_status_agenda  = 'E'
	where    nr_sequencia      = nr_sequencia_p
	and      coalesce(nr_atendimento::text, '') = '';

elsif (ie_atualizar_p = 'C') then
	update   agenda_consulta
	set      ie_status_agenda  =  'E'
        where    nr_sequencia      = nr_sequencia_p;

elsif (ie_atualizar_p = 'P') then
	update   agenda_paciente
	set      nr_atendimento    = nr_atendimento_p,
		 ie_status_agenda  = 'E'
	where    nr_sequencia      = nr_sequencia_p
	and      coalesce(nr_atendimento::text, '') = '';

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
