CREATE OR REPLACE PROCEDURE atualizar_status_agepac_auto (cd_pessoa_fisica_p text, ie_atualizou_dados_p text, cd_tipo_agenda_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_agenda_w	bigint;
nr_seq_agenda_w	agenda_paciente.nr_sequencia%type;			
			 
C01 CURSOR FOR
	SELECT  a.cd_agenda,
		b.nr_sequencia
	from	agenda a,
		agenda_paciente b
	where	b.ie_status_agenda not in ('L', 'C', 'B', 'LF','A','F','I')
	and	coalesce(b.nr_atendimento::text, '') = ''
	and	b.dt_agenda between trunc(clock_timestamp()) and trunc(clock_timestamp()) + 86399/86400
	and	b.cd_pessoa_fisica  = cd_pessoa_fisica_p
	and	a.cd_agenda = b.cd_agenda
	and	a.cd_tipo_agenda = cd_tipo_agenda_p
	and	trunc(b.dt_agenda) = trunc(clock_timestamp())
	order by b.dt_agenda;
			
			 

BEGIN

open C01;
loop
fetch C01 into	
	cd_agenda_w,
	nr_seq_agenda_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	CALL alterar_status_agenda(cd_agenda_w,nr_seq_agenda_w,'A',null,wheb_mensagem_pck.get_texto(799655),'N','Tasy');
	
	update	agenda_paciente
	set	ie_atualizou_autoatendimento = coalesce(ie_atualizou_dados_p,'N')
	where	nr_sequencia = nr_seq_agenda_w;
	
	end;
end loop;
close C01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
