CREATE OR REPLACE PROCEDURE atualizar_status_aval_marcas ( nm_usuario_p text, cd_estabelecimento_p bigint, cd_material_p bigint, nr_seq_motivo_p bigint, ds_justificativa_p text) AS $body$
DECLARE


nr_seq_status_aval_w	bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_status_aval_w
from	status_avaliacao_marca
where	ie_tipo_status = 'R'
and	nr_sequencia = nr_seq_motivo_p;

if (nr_seq_status_aval_w IS NOT NULL AND nr_seq_status_aval_w::text <> '') then

	update	material_marca
	set	nr_seq_status_aval = nr_seq_status_aval_w,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp(),
		ds_justificativa_padrao = ds_justificativa_p
	where (coalesce(cd_estabelecimento::text, '') = '' or coalesce(cd_estabelecimento_p,cd_estabelecimento) = cd_estabelecimento)
	and	(dt_validade_certificado_aprov IS NOT NULL AND dt_validade_certificado_aprov::text <> '')
	and	((trunc(dt_validade_certificado_aprov,'dd') < trunc(clock_timestamp(),'dd')) or
	((ie_vigente_anvisa = 'N') or (trunc(dt_validade_reg_anvisa,'dd') < trunc(clock_timestamp(),'dd'))))
	and	substr(obter_tipo_status_aval_marca(nr_seq_status_aval),1,1) <> 'R'
	and	coalesce(ie_situacao,'A') = 'A'
	and	coalesce(cd_material_p,cd_material) = cd_material;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
