CREATE OR REPLACE PROCEDURE atualizar_status_entrega_oci ( cd_fornecedor_p text, dt_prevista_entrega_p timestamp, ie_status_p text, nm_usuario_p text) AS $body$
DECLARE

/* 
ie_status_p 
S - Para confirmar a entrega 
N - Para não confirmar a entrega 
L - Para limpar a confirmação 
*/
 
 
nr_sequencia_w			bigint;

					 
c01 CURSOR FOR 
SELECT	distinct b.nr_sequencia 
from	ordem_compra a, 
	ordem_compra_item_entrega b 
where	a.nr_ordem_compra = b.nr_ordem_compra 
and	trunc(b.dt_prevista_entrega,'dd') = trunc(dt_prevista_entrega_p,'dd') 
and	(a.dt_aprovacao IS NOT NULL AND a.dt_aprovacao::text <> '') 
and	coalesce(a.cd_cgc_fornecedor,a.cd_pessoa_fisica) = cd_fornecedor_p 
and	coalesce(a.dt_baixa::text, '') = '' 
and	coalesce(a.nr_seq_motivo_cancel::text, '') = '' 
and	coalesce(b.dt_cancelamento::text, '') = '' 
and	b.qt_prevista_entrega > coalesce(b.qt_real_entrega,0) 
and not exists (   SELECT  1 
           from   ordem_compra_item i 
           where  b.nr_ordem_compra = i.nr_ordem_compra 
           and   b.nr_item_oci = i.nr_item_oci 
           and   (i.dt_reprovacao IS NOT NULL AND i.dt_reprovacao::text <> ''));


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	if (ie_status_p = 'L') then 
		update	ordem_compra_item_entrega 
		set	nm_usuario = nm_usuario_p, 
			dt_atualizacao = clock_timestamp(), 
			ie_status = '' 
		where	nr_sequencia = nr_sequencia_w;
	else 
		update	ordem_compra_item_entrega 
		set	nm_usuario = nm_usuario_p, 
			dt_atualizacao = clock_timestamp(), 
			ie_status = ie_status_p 
		where	nr_sequencia = nr_sequencia_w;
	end if;
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
