CREATE OR REPLACE PROCEDURE atualizar_status_ev_alerta ( nr_sequencia_p bigint, nr_seq_evento_p bigint, ie_status_p text) AS $body$
BEGIN

if (nr_seq_evento_p > 0) and (nr_sequencia_p > 0) then


	if (ie_status_p = 'L') then

		update	ev_evento_paciente
		set	ie_status = ie_status_p
		where	nr_sequencia = nr_sequencia_p
		and	nr_seq_evento = nr_seq_evento_p;

		update	ev_evento_pac_destino
		set	dt_atualizacao		=	clock_timestamp(),
			ie_status 		=	ie_status_p,
			dt_ciencia		=	clock_timestamp()
		where	nr_seq_ev_pac		=	nr_sequencia_p
		and	coalesce(dt_ciencia::text, '') = '';

	else


		update	ev_evento_paciente
		set	ie_status = ie_status_p
		where	nr_sequencia = nr_sequencia_p
		and	nr_seq_evento = nr_seq_evento_p;


	end if;
	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
