SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_status_exec (nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_laudo_w		bigint;
nr_prescricao_w		bigint;
nr_seq_prescricao_w	integer;
ie_status_w		smallint;



BEGIN

select	coalesce(max(nr_laudo),0),
	max(nr_prescricao),
	max(nr_sequencia_prescricao)
into STRICT	nr_laudo_w,
	nr_prescricao_w,
	nr_seq_prescricao_w
from	procedimento_paciente
where	nr_sequencia	= nr_sequencia_p;

if (nr_laudo_w = 0) then
	ie_status_w	:= 20;
else
	ie_status_w	:= 26;
end if;

select coalesce(max(CASE WHEN coalesce(a.dt_liberacao::text, '') = '' THEN ie_status_w  ELSE 40 END ),20)
into STRICT	ie_status_w
from	laudo_paciente a
where 	a.nr_sequencia = nr_laudo_w;

update	prescr_procedimento
set	ie_status_execucao	= ie_status_w,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao 		= clock_timestamp()
where	nr_prescricao = nr_prescricao_w
 and	nr_sequencia = nr_seq_prescricao_w;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
