CREATE OR REPLACE PROCEDURE atualizar_status_gestao_js ( ie_status_p text, nm_usuario_p text, nr_sequencia_p bigint) AS $body$
BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '')then
	begin
	
	if (coalesce(ie_status_p,'X') = 'P') then
		update  gestao_vaga
		set     ie_status      = ie_status_p,
			nm_resp_transf = nm_usuario_p,
			nm_usuario     = nm_usuario_p,
			nm_usuario_aprov = nm_usuario_p,
			dt_atualizacao = clock_timestamp(),
			dt_aprovacao   = clock_timestamp()
		where  	nr_sequencia   = nr_sequencia_p;
		
	elsif (coalesce(ie_status_p,'X') = 'D') then
		update  gestao_vaga
		set     ie_status      = ie_status_p,
			nm_resp_transf = nm_usuario_p,
			nm_usuario     = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where  	nr_sequencia   = nr_sequencia_p;	
	else
	
		update  gestao_vaga
		set     ie_status      = ie_status_p,
			dt_acomodacao  = clock_timestamp(),
			nm_resp_transf = nm_usuario_p,
			nm_usuario     = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where  	nr_sequencia   = nr_sequencia_p;
		
	end if;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
