CREATE OR REPLACE PROCEDURE atualizar_status_lab_lote ( nr_seq_lote_p bigint, ie_status_p bigint, nm_usuario_p text) AS $body$
BEGIN

update prescr_procedimento a
set ie_status_atend = ie_status_p
where exists (SELECT 1
		from lab_lote_exame_item b
		where b.nr_prescricao	= a.nr_prescricao
		  and b.nr_seq_prescr	= a.nr_sequencia
		  and b.nr_seq_lote		= nr_seq_lote_p)
  and ie_status_atend < ie_status_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
