SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_status_leito_se ( nr_atendimento_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (coalesce(nr_atendimento_p,0) > 0) then

	update	atend_paciente_unidade
	set	dt_saida_unidade 	= clock_timestamp(),
		dt_atualizacao 		= clock_timestamp(),
		nm_usuario 		= nm_usuario_p
	where	nr_atendimento 		= nr_atendimento_p
	and	coalesce(dt_saida_unidade::text, '') = ''
	and	obter_classif_setor(cd_setor_atendimento) = 11;


	update unidade_atendimento a
	set 	nr_atendimento      	 = NULL,
		dt_entrada_unidade  	 = NULL,
		ie_status_unidade   	= 'L',
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_atendimento 		= nr_atendimento_p
	and	obter_classif_setor(cd_setor_atendimento) = 11;

end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
