CREATE OR REPLACE PROCEDURE atualizar_status_nfe (cd_status_p text, nr_protocolo_p text, chave_acesso_p text, ie_enviada_p text, dt_recebimento_p timestamp) AS $body$
BEGIN

update nota_fiscal set	ie_status_nfe = cd_status_p,
			nr_nfe_imp_protocolo = nr_protocolo_p,
			ie_status_envio = CASE WHEN ie_enviada_p='S' THEN 'E'  ELSE 'N' END ,
			dt_autor_prot_nfe =  dt_recebimento_p
where	nr_danfe = chave_acesso_p;

commit;

end;
$body$
