CREATE OR REPLACE PROCEDURE atualizar_status_presc_proced ( ie_status_atend_p bigint, nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (ie_status_atend_p IS NOT NULL AND ie_status_atend_p::text <> '') and (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '') then
	begin
	update 	prescr_procedimento
	set 	ie_status_atend = ie_status_atend_p,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where (nr_prescricao, nr_sequencia) in (SELECT nr_prescricao, nr_seq_prescr
						  from 	 lab_lote_exame_item
						  where  nr_seq_lote = nr_seq_lote_p);
	end;
end if;

end;
$body$
