CREATE OR REPLACE PROCEDURE atualizar_status_reinf (nr_sequencia_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
BEGIN

if (ie_opcao_p = '1') then

	update fis_reinf_notas_r2010
	set    ie_status  = NULL,
	       dt_transmissao  = NULL
	where  nr_seq_superior = nr_sequencia_p
	and    ie_status = 'E';

elsif (ie_opcao_p = '2') then

	update fis_reinf_notas_r2020
	set    ie_status  = NULL,
	       dt_transmissao  = NULL
	where  nr_seq_superior = nr_sequencia_p
	and    ie_status = 'E';

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
