CREATE OR REPLACE PROCEDURE atualizar_status_reproc_intpd ( nr_sequencia_p bigint, ie_status_p text, nm_usuario_p text, ie_commit_p text default 'S', ie_reprocess_error_p text default 'S') AS $body$
DECLARE


C01 CURSOR FOR
SELECT	a.nr_seq_reg_alter
from	intpd_reg_alter_fila a,
        intpd_reg_alteracao b,
        intpd_reg_alter_campo c
where	a.nr_seq_fila		= nr_sequencia_p
and	a.nr_seq_reg_alter	= b.nr_sequencia
and	b.nr_sequencia		= c.nr_sequencia
and	c.ie_classificacao	= 'D'
order by 1;

c01_w	c01%rowtype;

BEGIN
if (coalesce(nr_sequencia_p,0) > 0) then
	update	intpd_fila_transmissao
	set	dt_atualizacao	= clock_timestamp(),
		nm_usuario = nm_usuario_p,
		ie_status = ie_status_p,
		ds_log  = NULL,
		ds_xml  = NULL,
		ds_xml_retorno  = NULL,
		cd_default_message  = NULL /*Limpa o codigo de erro quando reprocessa o registro*/
	where	nr_sequencia	= nr_sequencia_p
	and	((coalesce(ie_reprocess_error_p,'S') = 'N' and ie_status in ('E','D','P','S', 'A', 'AS', 'AP', 'R')) or (coalesce(ie_reprocess_error_p,'S') = 'S' and ie_status in ('E','D')));	


	delete	FROM INTPD_LOG_RECEBIMENTO
	where	NR_SEQ_FILA = nr_sequencia_p;
	
	open C01;
	loop
	fetch C01 into	
		c01_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		delete	from intpd_reg_alter_fila	where	nr_seq_reg_alter = c01_w.nr_seq_reg_alter;
		delete	from intpd_reg_alter_campo	where	nr_sequencia = c01_w.nr_seq_reg_alter;
		delete	from intpd_reg_alteracao	where	nr_sequencia = c01_w.nr_seq_reg_alter;
		end;
	end loop;
	close C01;
end if;

if ('S' = coalesce(ie_commit_p, 'S')) then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
