CREATE OR REPLACE PROCEDURE atualizar_status_terceiro ( nr_sequencia_p bigint, ie_tag_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (ie_tag_p = 200560) then

	update	conta_terceiro
	set	ie_status = '1',
		dt_atualizacao = clock_timestamp(),
		dt_liberacao  = NULL,
		nm_usuario_lib  = NULL,
		nm_usuario = nm_usuario_p
	where	nr_sequencia = nr_sequencia_p;

	update	pep_med_fatur
	set	dt_liberacao  = NULL,
		nm_usuario = nm_usuario_p
	where	nr_seq_conta_terceiro = nr_sequencia_p;

elsif (ie_tag_p = 200561) then

	update	conta_terceiro
	set	ie_status = '2',
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		dt_liberacao = clock_timestamp(),
		nm_usuario_lib = nm_usuario_p,
		nm_usuario_definitivo  = NULL,
		dt_fechamento  = NULL
	where	nr_sequencia = nr_sequencia_p;

	update	pep_med_fatur
	set	dt_liberacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_seq_conta_terceiro = nr_sequencia_p;

elsif (ie_tag_p = 200562) then

	update	conta_terceiro
	set	ie_status = '3',
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		nm_usuario_definitivo = nm_usuario_p,
		dt_fechamento = clock_timestamp()
	where	nr_sequencia = nr_sequencia_p;

end if;

commit;

end;
