CREATE OR REPLACE PROCEDURE atualizar_submotivo_alta ( nr_atendimento_p bigint, cd_submotivo_p bigint, nm_usuario_p text, ie_opcao_p text) AS $body$
DECLARE

nr_seq_alta_hist_w	bigint;

BEGIN

CALL gerar_int_padrao.set_executando_recebimento('S');

if (coalesce(nr_atendimento_p,0) > 0) and (coalesce(cd_submotivo_p,0) > 0) and (ie_opcao_p  = 'G') then

	update	atendimento_paciente
	set		nr_submotivo_alta = cd_submotivo_p
	where	nr_atendimento 	= nr_atendimento_p;


elsif (coalesce(nr_atendimento_p,0) > 0) and (ie_opcao_p  = 'D') then

		update	atendimento_paciente
		set		nr_submotivo_alta  = NULL
		where	nr_atendimento 	  = nr_atendimento_p;

end if;

commit;

CALL gerar_int_padrao.set_executando_recebimento('N');

end;
