CREATE OR REPLACE PROCEDURE atualizar_sup_lanc_cartao ( nr_sequencia_p bigint, dt_liberacao_p text, nr_atendimento_p bigint, ds_nome_paciente_p text, ie_campo_p text, nm_usuario_p text) AS $body$
DECLARE

	w_ie_update	smallint := 0;

BEGIN
-- Valores IE_CAMPO_P 
-- 	CPR = cd_pessoa_resp 
--	DTL = dt_liberacao 
--	NRA = nr_atendimento e ds_nome_paciente 
--	DTE = Data de estorno da liberação. 
 
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then 
	begin 
	 
	if (ie_campo_p = 'CPR') then 
		begin 
		update 	sup_lanc_cartao 
		set  	cd_pessoa_resp 	= (SELECT obter_pessoa_fisica_usuario(nm_usuario_p, 'C') ), 
			nm_usuario 	= nm_usuario_p, 
			dt_atualizacao 	= clock_timestamp() 
		where 	nr_sequencia 	= nr_sequencia_p;
		 
		w_ie_update := 1;
		end;
	end if;
	 
	if (ie_campo_p = 'DTL') then 
		begin 
		update	sup_lanc_cartao 
		set	dt_liberacao 	= clock_timestamp(), 
			nm_usuario 	= nm_usuario_p, 
			dt_atualizacao 	= clock_timestamp() 
		where	nr_sequencia 	= nr_sequencia_p;
		 
		w_ie_update := 1;
		end;
	end if;
	 
	if (ie_campo_p = 'DTE') then 
		begin 
		update	sup_lanc_cartao 
		set	dt_liberacao 	 = NULL, 
			nm_usuario 	= nm_usuario_p, 
			dt_atualizacao 	= clock_timestamp() 
		where	nr_sequencia 	= nr_sequencia_p;
		 
		w_ie_update := 1;
		end;
	end if;
 
	if (ie_campo_p = 'NRA') then 
		begin 
		update 	sup_lanc_cartao 
		set  	nr_atendimento 		= nr_atendimento_p, 
			ds_nome_paciente 	= ds_nome_paciente_p, 
			nm_usuario 		= nm_usuario_p, 
			dt_atualizacao 		= clock_timestamp()		 
		where nr_sequencia 		= nr_sequencia_p;
		 
		w_ie_update := 1;
		end;
	end if;
 
	if (w_ie_update > 0) then 
		begin 
		commit;
		end;
	end if;
	 
	end;
end if;
end;
$body$
