SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_susp_medic_padrao (nm_usuario_p text) AS $body$
DECLARE


nr_seq_medicacao_w	bigint;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	medic_uso_continuo
	where (dt_inicio + nr_dias_uso) < clock_timestamp()
	and 	(nr_dias_uso IS NOT NULL AND nr_dias_uso::text <> '')
	and	coalesce(dt_suspensao::text, '') = ''
	and	ie_uso_continuo = 'N';

BEGIN

open c01;
loop
fetch c01 into
	nr_seq_medicacao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	update 	medic_uso_continuo
	set	dt_suspensao = clock_timestamp(),
		nm_usuario_susp = nm_usuario_p
	where	nr_sequencia = nr_seq_medicacao_w;

end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
