CREATE OR REPLACE PROCEDURE atualizar_tabela_custo_conta (NR_INTERNO_CONTA_P bigint) AS $body$
DECLARE

DT_ATUALIZACAO_W        	timestamp := clock_timestamp();
cd_estabelecimento_w		smallint;
cd_tabela_custo_w			integer;


BEGIN

/* Obter tabela atual */

begin
select
	cd_estabelecimento,
	cd_tabela_venda
into STRICT 	cd_estabelecimento_w,
	cd_tabela_custo_w
from parametro_custo;
exception
	when others then
   		cd_tabela_custo_w  := null;
end;

if (cd_tabela_custo_w IS NOT NULL AND cd_tabela_custo_w::text <> '') then
	begin
	update procedimento_paciente
   	set	cd_estabelecimento_custo= cd_estabelecimento_w,
		cd_tabela_custo		= cd_tabela_custo_w
	where	nr_interno_conta 		= nr_interno_conta_p;

	update material_atend_paciente
   	set	cd_estabelecimento_custo= cd_estabelecimento_w,
		cd_tabela_custo		= cd_tabela_custo_w
	where	nr_interno_conta 		= nr_interno_conta_p;
	end;
end if;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
