SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_tabela_visao_util () AS $body$
DECLARE


ds_form_w			varchar(255);
nr_seq_visao_w		bigint;
nm_objeto_w			varchar(255);
ds_pasta_objeto_w	varchar(255);
nm_funcao_w			varchar(255);
cd_funcao_w			bigint;
nr_seq_objeto_w		bigint;

c01 CURSOR FOR
SELECT	ds_form,
		nr_seq_visao,
		nm_objeto
from	tabela_visao_utilizacao
order by 1,2;


BEGIN


open C01;
loop
fetch C01 into
	ds_form_w,
	nr_seq_visao_w,
	nm_objeto_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	select	max(ds_funcao),
			max(cd_funcao)
	into STRICT	nm_funcao_w,
			cd_funcao_w
	from	funcao
	where	ds_form = ds_form_w;

	select	max(obter_desc_expressao(cd_exp_texto)),
			max(nr_sequencia)
	into STRICT	ds_pasta_objeto_w,
			nr_seq_objeto_w
	from	dic_objeto
	where	cd_funcao = cd_funcao_w
	and		nm_objeto = nm_objeto_w;

	update	tabela_visao_utilizacao
	set		nm_funcao		= nm_funcao_w,
			nr_seq_objeto	= nr_seq_objeto_w,
			ds_pasta_objeto = ds_pasta_objeto_w
	where	ds_form			= ds_form_w
	and		nr_seq_visao	= nr_seq_visao_w
	and		nm_objeto		= nm_objeto_w;

	commit;

end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
