CREATE OR REPLACE PROCEDURE atualizar_tab_mat_serv ( nm_tabela_p text, cd_estabelecimento_p bigint, cd_estab_destino_p bigint, cd_tabela_origem_p bigint, cd_tabela_destino_p bigint, ds_tabela_Mat_Serv_p text, ie_copiar_p text, ie_situacao_p text, ie_hosp_pls_p text, ie_atualiza_nf_p text, nm_usuario_p text) AS $body$
DECLARE


qt_reg_w	bigint;


BEGIN

qt_reg_w	:= 0;

if (nm_tabela_p = 'TABELA_SERVICO') then
	begin
	select	count(*)
	into STRICT	qt_reg_w
	from 	tabela_servico
	where	cd_estabelecimento	=	cd_estab_destino_p
	and 	cd_tabela_servico	=	cd_tabela_destino_p;

	if (qt_reg_w = 0) then
		begin
		insert into tabela_servico(cd_estabelecimento,
			cd_tabela_servico,
			ds_tabela_servico,
			ie_situacao,
			dt_atualizacao,
			nm_usuario,
			ie_hosp_pls)
		values (cd_estab_destino_p,
			cd_tabela_destino_p,
			ds_tabela_Mat_Serv_p,
			ie_situacao_p,
			clock_timestamp(),
			nm_usuario_p,
			ie_hosp_pls_p);
		commit;
		end;
	end if;

	if (ie_copiar_p = 'S') then
		delete	from preco_servico
		where	cd_estabelecimento	= cd_estab_destino_p
		and	cd_tabela_servico	= cd_tabela_destino_p;
	end if;

	insert into preco_servico(cd_estabelecimento,
		cd_tabela_servico,
		cd_procedimento,
		dt_inicio_vigencia,
		vl_servico,
		cd_moeda,
		dt_atualizacao,
		nm_usuario,
		ie_origem_proced,
		cd_unidade_medida,
		DT_INATIVACAO,
		DT_VIGENCIA_FINAL)
	(SELECT cd_estab_destino_p,
		cd_tabela_destino_p,
		cd_procedimento,
		dt_inicio_vigencia,
		vl_servico,
		cd_moeda,
		clock_timestamp(),
		nm_usuario_p,
		ie_origem_proced,
		cd_unidade_medida,
		DT_INATIVACAO,
		DT_VIGENCIA_FINAL
	 from	preco_servico
	 where	cd_estabelecimento	= cd_estabelecimento_p
	 and	cd_tabela_servico	= cd_tabela_origem_p);

	commit;

	end;
end if;


if (nm_tabela_p = 'TABELA_PRECO_MATERIAL') then
	begin
	select	count(*)
	into STRICT	qt_reg_w
	from 	tabela_preco_material
	where	cd_estabelecimento	=	cd_estab_destino_p
	and 	cd_tab_preco_mat	=	cd_tabela_origem_p;

	if (qt_reg_w = 0) then
		begin
		insert into tabela_preco_material(cd_estabelecimento,
			cd_tab_preco_mat,
			ds_tab_preco_mat,
			ie_situacao,
			dt_atualizacao,
			nm_usuario,
			ie_atualiza_nf,
			ie_hosp_pls)
		values (cd_estab_destino_p,
			cd_tabela_origem_p,
			ds_tabela_Mat_Serv_p,
			ie_situacao_p,
			clock_timestamp(),
			nm_usuario_p,
			ie_atualiza_nf_p,
			ie_hosp_pls_p);
		end;
	end if;

	if (ie_copiar_p = 'S') then
		delete from preco_material
		where	cd_estabelecimento	= cd_estab_destino_p
		and	cd_tab_preco_mat	= cd_tabela_destino_p;
	end if;

	insert into preco_material(cd_estabelecimento,
		cd_tab_preco_mat,
		cd_material,
		dt_inicio_vigencia,
		vl_preco_venda,
		cd_moeda,
		ie_brasindice,
		dt_atualizacao,
		nm_usuario,
		cd_unidade_medida,
		ie_situacao,
		nr_sequencia_nf,
		nr_item_nf,
		cd_cgc_fornecedor,
		dt_final_vigencia)
	(SELECT cd_estab_destino_p,
		cd_tabela_destino_p,
		cd_material,
		dt_inicio_vigencia,
		vl_preco_venda,
		cd_moeda,
		ie_brasindice,
		clock_timestamp(),
		nm_usuario_p,
		cd_unidade_medida,
		ie_situacao,
		nr_sequencia_nf,
		nr_item_nf,
		cd_cgc_fornecedor,
		dt_final_vigencia
	 from	preco_material
	 where	cd_estabelecimento	= cd_estabelecimento_p
	 and	cd_tab_preco_mat	= cd_tabela_origem_p);

	commit;

	end;
end if;

end;
$body$
