SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_taxa_esp_mat (nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
/* atualiza taxa sobre matmed no momento do consumo do mesmo */
 
/* Pró não quer geração desta taxa somente no fechamento da conta */
 
qt_reg_regra_w			bigint;
cd_convenio_w			integer;
cd_material_w			integer;
cd_grupo_material_w		smallint;
cd_subgrupo_material_w		smallint;
cd_classe_material_w		integer;
nr_atendimento_w		bigint;
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
nr_sequencia_w			bigint;
nr_seq_proc_pacote_w		bigint;
ie_consignado_w			varchar(1);
vl_material_w			double precision;
cd_estabelecimento_w		smallint;
ie_responsavel_credito_w	varchar(5);


BEGIN 
 
/* Verifica se existe alguma regra de preço especial composta */
 
 
select	count(*) 
into STRICT	qt_reg_regra_w 
from	regra_preco_proc;
 
if (qt_reg_regra_w	> 0)	then 
	BEGIN 
 
	/* Busca dados da material atend paciente */
 
	select	a.cd_convenio, 
		a.cd_material, 
		b.cd_grupo_material, 
		b.cd_subgrupo_material, 
		b.cd_classe_material, 
		a.nr_atendimento, 
		a.nr_seq_proc_pacote, 
		b.ie_consignado, 
		a.vl_material, 
		a.ie_responsavel_credito 
	into STRICT	cd_convenio_w, 
		cd_material_w, 
		cd_grupo_material_w, 
		cd_subgrupo_material_w, 
		cd_classe_material_w, 
		nr_atendimento_w, 
		nr_seq_proc_pacote_w, 
		ie_consignado_w, 
		vl_material_w, 
		ie_responsavel_credito_w 
	from	estrutura_material_v b, 
		material_atend_paciente a 
	where	a.nr_sequencia		= nr_sequencia_p 
	and	a.cd_material		= b.cd_material;
	 
	select	max(cd_estabelecimento) 
	into STRICT	cd_estabelecimento_w 
	from	conta_paciente b, 
		material_atend_paciente a 
	where	a.nr_interno_conta = b.nr_interno_conta 
	and	a.nr_sequencia = nr_sequencia_p;
 
	/* Verifica se este material pertence a regra existente */
 
	select 	coalesce(max(b.cd_procedimento),0) 
	into STRICT	cd_procedimento_w 
	from	regra_ajuste_proc b, 
		regra_preco_proc_crit a 
	where (a.cd_material		= cd_material_w			or 
		a.cd_classe_material	= cd_classe_material_w		or 
		a.cd_subgrupo_material	= cd_subgrupo_material_w	or 
		a.cd_grupo_material	= cd_grupo_material_w) 
	and	((((coalesce(a.ie_consignado,'2') = '0') and (ie_consignado_w = '0')) or 
		((coalesce(a.ie_consignado,'2') = '1') and (ie_consignado_w = '1')) or 
		((coalesce(a.ie_consignado,'2') = '3') and (ie_consignado_w = '2')) or --Criada nova opcao OS821589			 
		(coalesce(a.ie_consignado,'2') = '2'))) 
	and	b.nr_seq_regra_preco	= a.nr_seq_regra 
	and 	coalesce(vl_material_w,0) between coalesce(a.vl_inicial, coalesce(vl_material_w,0)) and coalesce(a.vl_final, coalesce(vl_material_w,0)) 
	and	coalesce(a.cd_estabelecimento, coalesce(cd_estabelecimento_w, 0)) = coalesce(cd_estabelecimento_w, 0) 
	and 	coalesce(a.ie_responsavel_credito, coalesce(ie_responsavel_credito_w,'0')) = coalesce(ie_responsavel_credito_w,'0') 
	and	b.cd_convenio		= cd_convenio_w 
	and	coalesce(a.cd_convenio, coalesce(cd_convenio_w, 0)) = coalesce(cd_convenio_w, 0) 
	and	b.ie_situacao		= 'A' 
	and	((a.ie_pacote		= 'A') or 
		 (a.ie_pacote		= 'S' AND nr_seq_proc_pacote_w IS NOT NULL AND nr_seq_proc_pacote_w::text <> '') or 
		 ((a.ie_pacote		= 'N') and (coalesce(nr_seq_proc_pacote_w::text, '') = '')));
 
	if (cd_procedimento_w > 0)	then 
		begin 
		 
		begin 
		delete 	from procedimento_paciente 
		where	nr_atendimento	= nr_atendimento_w 
		and	cd_procedimento	= cd_procedimento_w;	
		exception 	when others then 
			cd_procedimento_w	:= cd_procedimento_w;
		commit;
		 
		end;
 
		/* Encontrada regra para este material */
 
		CALL gerar_lancamento_automatico(nr_atendimento_w, 
							null,36,nm_usuario_p,null,null,null,null,null,null);
		END;
	end if;
	END;
end if;
commit;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE atualizar_taxa_esp_mat (nr_sequencia_p bigint, nm_usuario_p text) FROM PUBLIC;

