CREATE OR REPLACE PROCEDURE atualizar_tempo_senha_tasy ( cd_estabelecimento_p bigint, ie_tipo_atendimento_p bigint, nr_fila_p text, nr_senha_p text, cd_tempo_p text, dt_tempo_p timestamp, nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_sequencia_w	bigint;

cd_estabelecimento_w	smallint;
ie_tipo_atendimento_w	smallint;
nr_fila_w		varchar(20);
nr_senha_w		varchar(20);
cd_tempo_w		varchar(10);
dt_tempo_w		timestamp;
nr_atendimento_w	bigint;
nm_usuario_w		varchar(15);


BEGIN 
 
cd_estabelecimento_w	:= cd_estabelecimento_p;
ie_tipo_atendimento_w	:= ie_tipo_atendimento_p;
nr_fila_w		:= nr_fila_p;
nr_senha_w		:= nr_senha_p;
cd_tempo_w		:= cd_tempo_p;
dt_tempo_w		:= dt_tempo_p;
nr_atendimento_w	:= nr_atendimento_p;
nm_usuario_w		:= nm_usuario_p;
 
if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then 
	select	ds_senha_qmatic, 
		ie_tipo_atendimento 
	into STRICT	nr_senha_w, 
		ie_tipo_atendimento_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_w;
 
else	 
	select	coalesce(max(nr_atendimento),null) 
	into STRICT	nr_atendimento_w 
	from	tempo_senha_atend 
	where	trunc(dt_tempo)		= trunc(clock_timestamp()) 
	and	nr_fila			= nr_fila_w 
	and	nr_senha		= nr_senha_w 
	and	ie_tipo_atendimento	= ie_tipo_atendimento_w;
end if;
 
if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then 
 
	select	max(coalesce(nr_fila,null)) 
	into STRICT	nr_fila_w 
	from	tempo_senha_atend 
	where	nr_atendimento = nr_atendimento_w;
	 
	if (nr_fila_w IS NOT NULL AND nr_fila_w::text <> '') then 
		select	nextval('tempo_senha_atend_seq') 
		into STRICT	nr_sequencia_w 
		;
 
		insert into tempo_senha_atend(	 
			nr_sequencia, 	 
			cd_estabelecimento, 
			nr_fila,		 
			nr_senha, 
			cd_tempo,		 
			dt_tempo, 
			dt_atualizacao,	 
			nm_usuario, 
			dt_atualizacao_nrec,	 
			nm_usuario_nrec, 
			nr_atendimento,	 
			ie_tipo_atendimento) 
		values (	nr_sequencia_w,	 
			cd_estabelecimento_w, 
			nr_fila_w,		 
			nr_senha_w, 
			cd_tempo_w,		 
			clock_timestamp(), 
			clock_timestamp(),		 
			nm_usuario_w, 
			clock_timestamp(),		 
			nm_usuario_w, 
			nr_atendimento_w,	 
			ie_tipo_atendimento_w);
		commit;
	end if;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
