CREATE OR REPLACE PROCEDURE atualizar_tipo_audit_item ( nr_sequencia_p bigint, ie_tipo_item_p text, ie_tipo_auditoria_p text, nm_usuario_p text) AS $body$
BEGIN

if (ie_tipo_item_p = 'P') then

	update	auditoria_propaci
	set	ie_tipo_auditoria = ie_tipo_auditoria_p,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_sequencia = nr_sequencia_p;

else

	update	auditoria_matpaci
	set	ie_tipo_auditoria = ie_tipo_auditoria_p,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_sequencia = nr_sequencia_p;

end if;

commit;

end;
$body$
