CREATE OR REPLACE PROCEDURE atualizar_tipo_guia_conta (nm_usuario_p text, nr_interno_conta_p bigint, ie_tipo_guia_p text, ie_atualizar_atend_p text) AS $body$
DECLARE

			 
nr_atendimento_w	conta_paciente.nr_atendimento%type;
			

BEGIN 
 
if (ie_tipo_guia_p IS NOT NULL AND ie_tipo_guia_p::text <> '') then 
 
	update	procedimento_paciente 
	set	ie_tipo_guia		= ie_tipo_guia_p, 
		dt_atualizacao		= clock_timestamp(), 
		nm_usuario		= nm_usuario_p 
	where	nr_interno_conta	= nr_interno_conta_p;
	 
	update	material_atend_paciente 
	set	ie_tipo_guia		= ie_tipo_guia_p, 
		dt_atualizacao		= clock_timestamp(), 
		nm_usuario		= nm_usuario_p 
	where	nr_interno_conta	= nr_interno_conta_p;
	 
	update	conta_paciente 
	set	ie_tipo_guia		= ie_tipo_guia_p, 
		dt_atualizacao		= clock_timestamp(), 
		nm_usuario		= nm_usuario_p 
	where	nr_interno_conta	= nr_interno_conta_p;
	 
	if (ie_atualizar_atend_p = 'S') then 
	 
		select	max(nr_atendimento) 
		into STRICT	nr_atendimento_w 
		from	conta_paciente 
		where	nr_interno_conta	= nr_interno_conta_p;
	 
		update	atend_categoria_convenio 
		set	ie_tipo_guia	= ie_tipo_guia_p, 
			dt_atualizacao	= clock_timestamp(), 
			nm_usuario	= nm_usuario_p 
		where	nr_atendimento	= nr_atendimento_w;
	 
	end if;
	 
	commit;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
