CREATE OR REPLACE PROCEDURE atualizar_tipo_guia_convenio ( cd_estabelecimento_p bigint, nr_atendimento_p bigint, ie_tipo_guia_p text, ie_clinica_p bigint, nm_usuario_p text) AS $body$
DECLARE

ie_liberado_w	varchar(1);


BEGIN

if (coalesce(nr_atendimento_p,0) > 0)  then

	ie_liberado_w := Obter_Lib_Tipo_guia(cd_estabelecimento_p, ie_tipo_guia_p, ie_clinica_p);

	if (ie_liberado_w = 'N') then
		update	atend_categoria_convenio
		set	ie_tipo_guia  = NULL
		where 	nr_atendimento = nr_atendimento_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
