CREATE OR REPLACE PROCEDURE atualizar_tipo_lista_simpro ( cd_simpro_p bigint, dt_vigencia_p timestamp, ie_tipo_lista_p text, nm_usuario_p text) AS $body$
DECLARE


cd_estab_simpro_w	estabelecimento.cd_estabelecimento%type;


BEGIN

cd_estab_simpro_w := wheb_usuario_pck.get_cd_estabelecimento;

if (coalesce(cd_simpro_p,0) > 0) then

	update	simpro_preco
	set	ie_tipo_lista = ie_tipo_lista_p,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	cd_simpro = cd_simpro_p
	and	dt_vigencia = dt_vigencia_p
	and	coalesce(cd_estabelecimento, coalesce(cd_estab_simpro_w, 0)) = coalesce(cd_estab_simpro_w, 0);

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
