CREATE OR REPLACE PROCEDURE atualizar_tipo_rads_laudo (CD_RADS_P text, DS_RADS_P text, NR_LAUDO_P bigint, NM_USUARIO_P text) AS $body$
DECLARE


IE_EXISTE_REGISTRO smallint;


BEGIN
  SELECT coalesce(MAX(1), 0)
  INTO STRICT IE_EXISTE_REGISTRO
  FROM LAUDO_PACIENTE_COMPL
  WHERE NR_SEQ_LAUDO = NR_LAUDO_P;

	IF (IE_EXISTE_REGISTRO = 1) THEN
    IF (DS_RADS_P = 'PI') THEN
		  UPDATE LAUDO_PACIENTE_COMPL
			SET IE_PIRADS = CD_RADS_P,
          DT_ATUALIZACAO = clock_timestamp(),
          NM_USUARIO = NM_USUARIO_P
			WHERE NR_SEQ_LAUDO = NR_LAUDO_P;
		ELSIF (DS_RADS_P = 'TI') THEN
		  UPDATE LAUDO_PACIENTE_COMPL
			SET IE_TIRADS = CD_RADS_P,
          DT_ATUALIZACAO = clock_timestamp(),
          NM_USUARIO = NM_USUARIO_P
      WHERE NR_SEQ_LAUDO = NR_LAUDO_P;
    END IF;
  ELSE
    IF (DS_RADS_P = 'PI') THEN
      INSERT INTO LAUDO_PACIENTE_COMPL(NR_SEQUENCIA,
                                       NR_SEQ_LAUDO,
                                       DT_ATUALIZACAO,
                                       NM_USUARIO,
                                       DT_ATUALIZACAO_NREC,
                                       NM_USUARIO_NREC,
                                       IE_PIRADS)
      VALUES (nextval('laudo_paciente_compl_seq'),
             NR_LAUDO_P,
             clock_timestamp(),
             NM_USUARIO_P,
             clock_timestamp(),
             NM_USUARIO_P,
             CD_RADS_P);
    ELSIF (DS_RADS_P = 'TI') THEN
      INSERT INTO LAUDO_PACIENTE_COMPL(NR_SEQUENCIA,
                                       NR_SEQ_LAUDO,
                                       DT_ATUALIZACAO,
                                       NM_USUARIO,
                                       DT_ATUALIZACAO_NREC,
                                       NM_USUARIO_NREC,
                                       IE_TIRADS)
      VALUES (nextval('laudo_paciente_compl_seq'),
             NR_LAUDO_P,
             clock_timestamp(),
             NM_USUARIO_P,
             clock_timestamp(),
             NM_USUARIO_P,
             CD_RADS_P);
    END IF;

    COMMIT;
  END IF;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
