SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_titulo_pagar ( nr_bordero_p bigint, vl_desconto_bordero_p bigint, nr_titulo_p bigint, cd_tipo_baixa_p bigint, ie_atualizar_p bigint) AS $body$
BEGIN
if (nr_bordero_p IS NOT NULL AND nr_bordero_p::text <> '') and (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then

	if (ie_atualizar_p = 1) then

		update	titulo_pagar
		set	nr_bordero		= nr_bordero_p,
			vl_bordero		= vl_saldo_titulo,
			vl_juros_bordero	= vl_saldo_juros,
			vl_multa_bordero	= vl_saldo_multa,
			vl_desconto_bordero	= vl_desconto_bordero_p
		where	coalesce(nr_bordero::text, '') = ''
		and	nr_titulo		= nr_titulo_p;

		update	titulo_pagar
		set	cd_tipo_baixa_neg	= cd_tipo_baixa_p
		where	nr_titulo		= nr_titulo_p;

		commit;

	elsif (ie_atualizar_p = 2) then

		update	titulo_pagar
		set	cd_tipo_baixa_neg	= cd_tipo_baixa_p
		where	nr_titulo		= nr_titulo_p
		and	coalesce(cd_tipo_baixa_neg::text, '') = '';

		commit;

	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
