SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_tit_pagar_classif (nr_titulo_p bigint, ie_commit_p text, nm_usuario_p text) AS $body$
DECLARE


vl_descontos_w		double precision;
vl_alteracao_w		double precision;
vl_titulo_w		double precision;
nr_sequencia_w		bigint;
vl_original_w		double precision;
vl_classificacao_w	double precision;
VL_DESCONTO_classif_w	double precision;
vl_base_classif_w	double precision;
vl_total_w		double precision;
vl_baixa_w		double precision;
vl_tributo_w		double precision;
vl_acrescimos_w		double precision;
vl_acrescimo_classif_w	double precision;
vl_total_desconto_w	double precision;
IE_TRIB_ATUALIZA_SALDO_w	varchar(3);
vl_adiantamento_w		double precision := 0;

vl_alterado_w		double precision;

qt_trib_repasse_w			bigint;
qt_trib_nf_w				bigint;
nr_seq_nota_fiscal_w		titulo_pagar.nr_seq_nota_fiscal%type;
qt_trib_lote_pag_pls_w		bigint;
nr_seq_lote_res_pls_w		titulo_pagar.nr_seq_lote_res_pls%type;
qt_trib_pls_pag_prest_w		bigint;
nr_seq_pls_pag_prest_w		titulo_pagar.nr_seq_pls_pag_prest%type;
qt_trib_ordem_w				bigint;
nr_ordem_compra_w			titulo_pagar.nr_ordem_compra%type;
ie_trib_saldo_tit_nf_w		parametros_contas_pagar.ie_trib_saldo_tit_nf%type;	
nr_repasse_terceiro_w		titulo_pagar.nr_repasse_terceiro%type;
cd_estabelecimento_w		parametros_contas_pagar.cd_estabelecimento%type;
vl_total_acrescimo_w		titulo_pagar_classif.vl_acrescimo%type;
nr_sequencia_maior_valor_w titulo_pagar_classif.nr_sequencia%type;


c01 CURSOR FOR
SELECT	nr_sequencia,
	coalesce(vl_original,0),
	vl_titulo,
	VL_DESCONTO
from	titulo_pagar_classif
where	nr_titulo	= nr_titulo_p
order	by nr_sequencia;

c02 CURSOR FOR
SELECT	vl_alteracao
from	titulo_pagar_alt_valor
where	nr_titulo	= nr_titulo_p
order	by nr_sequencia;


BEGIN

select	max(a.vl_titulo),
		max(b.IE_TRIB_ATUALIZA_SALDO),
		coalesce(max(b.ie_trib_saldo_tit_nf),'N'),
		max(b.cd_estabelecimento)
into STRICT	vl_titulo_w,
		IE_TRIB_ATUALIZA_SALDO_w,
		ie_trib_saldo_tit_nf_w,
		cd_estabelecimento_w
from	parametros_contas_pagar b,
	titulo_pagar a
where	a.nr_titulo		= nr_titulo_p
and	a.cd_estabelecimento	= b.cd_estabelecimento;

vl_base_classif_w		:= vl_titulo_w;

if (coalesce(cd_estabelecimento_w::text, '') = '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(189078);
end if;

/*open c02;
loop
fetch c02 into
	vl_alteracao_w;
exit when c02%notfound;
	vl_base_classif_w	:= vl_alteracao_w;
end loop;
close c02;
Francisco - OS 89125 - 11/04/2008 - Troquei o cursor  pelo select abaixo
*/
select	max(vl_alteracao)
into STRICT	vl_alterado_w
from	titulo_pagar_alt_valor
where	nr_titulo	= nr_titulo_p
and nr_sequencia = (SELECT max(nr_sequencia) from titulo_pagar_alt_valor where nr_titulo = nr_titulo_p );

vl_base_classif_w	:=    vl_alterado_w;

if (coalesce(vl_base_classif_w::text, '') = '') then
	vl_base_classif_w := vl_titulo_w;
end if;

select	coalesce(sum(coalesce(VL_JUROS,0) + coalesce(VL_MULTA,0) + coalesce(VL_OUTROS_ACRESCIMOS,0)),0),
	coalesce(sum(coalesce(vl_descontos,0) + coalesce(VL_OUTRAS_DEDUCOES,0)),0),
	coalesce(sum(vl_baixa),0)
into STRICT	vl_acrescimos_w,
	vl_descontos_w,
	vl_baixa_w
from	titulo_pagar_baixa
where	nr_titulo	= nr_titulo_p;

select	max(a.nr_repasse_terceiro),
		max(a.nr_seq_nota_fiscal),
		max(a.nr_seq_lote_res_pls),
		max(a.nr_seq_pls_pag_prest),
		max(a.nr_ordem_compra)
into STRICT	nr_repasse_terceiro_w,
		nr_seq_nota_fiscal_w,
		nr_seq_lote_res_pls_w,
		nr_seq_pls_pag_prest_w,
		nr_ordem_compra_w
from	titulo_pagar a
where	a.nr_titulo = nr_titulo_p;

vl_tributo_w		:= 0;
/*OS 1586772 - Fiz aqui igual na consistir_tit_pagar_baixa*/

if (IE_TRIB_ATUALIZA_SALDO_w = 'S') then

	select	count(*)
	into STRICT	qt_trib_repasse_w
	from	repasse_terceiro_venc b,
			repasse_terc_venc_trib a
	where	a.nr_seq_rep_venc		= b.nr_sequencia
	and		b.nr_repasse_terceiro	= nr_repasse_terceiro_w;
	
	select	count(*)
	into STRICT	qt_trib_nf_w
	from	nota_fiscal_trib a
	where	a.nr_sequencia	= nr_seq_nota_fiscal_w;
	
	if (qt_trib_nf_w = 0) then
		select	count(*)
		into STRICT	qt_trib_nf_w
		from	nota_fiscal_item_trib a
		where	nr_sequencia	= nr_seq_nota_fiscal_w;
	end if;
	
	if (qt_trib_nf_w = 0) then
		select	count(*)
		into STRICT	qt_trib_nf_w
		from	nota_fiscal_venc_trib b,
				nota_fiscal_venc a
		where	a.nr_sequencia	= nr_seq_nota_fiscal_w
		and		a.nr_sequencia	= b.nr_sequencia;
	end if;
	
	select	count(*)
	into STRICT	qt_trib_lote_pag_pls_w
	from	pls_lote_protocolo_venc b,
			pls_lote_prot_venc_trib a
	where	a.nr_seq_lote_venc	= b.nr_sequencia
	and		b.nr_seq_lote		= nr_seq_lote_res_pls_w
	and	exists (SELECT	1
			from	titulo_pagar_imposto x
			where	x.nr_titulo	= b.nr_titulo
			and	x.dt_atualizacao_nrec > a.dt_atualizacao_nrec);
			
	select	count(*)
	into STRICT	qt_trib_pls_pag_prest_w		
	from	pls_pag_prest_venc_trib	c,
			pls_pag_prest_vencimento	b,
			pls_pagamento_prestador	a
	where	a.nr_sequencia	= b.nr_seq_pag_prestador
	and		b.nr_sequencia	= c.nr_seq_vencimento
	and		a.nr_sequencia	= nr_seq_pls_pag_prest_w;	

	select	count(*)
	into STRICT	qt_trib_ordem_w
	from	ordem_compra_item_trib a
	where	nr_ordem_compra = nr_ordem_compra_w;	

	if (coalesce(nr_repasse_terceiro_w,0) = 0 or qt_trib_repasse_w = 0) and		-- edgar , qdo o titulo Ã© gerado o sistema gera o titulo com valor limpo
		(coalesce(nr_seq_nota_fiscal_w,0) = 0 or qt_trib_nf_w = 0) and (coalesce(nr_seq_lote_res_pls_w,0) = 0 or qt_trib_lote_pag_pls_w = 0) and (coalesce(nr_seq_pls_pag_prest_w,0) = 0 or qt_trib_pls_pag_prest_w = 0) and (coalesce(nr_ordem_compra_w,0) = 0 or (qt_trib_ordem_w = 0))	then

		select	coalesce(sum(a.vl_imposto),0)
		into STRICT	vl_tributo_w
		from	tributo b,
				titulo_pagar_imposto a
		where	a.nr_titulo						= nr_titulo_p
		and		a.cd_tributo					= b.cd_tributo
		and		a.ie_pago_prev					= 'V'
		and		coalesce(b.ie_saldo_tit_pagar,'S')	= 'S';
		
	else

		if (ie_trib_saldo_tit_nf_w = 'N') then
			select	coalesce(sum(vl_imposto),0)
			into STRICT	vl_tributo_w
			from	titulo_pagar_imposto a,
					tributo b
			where	a.cd_tributo = b.cd_tributo
			and		nr_titulo	= nr_titulo_p
			and		ie_pago_prev	= 'V'
			and		(nr_seq_baixa IS NOT NULL AND nr_seq_baixa::text <> '')
			and		coalesce(b.ie_saldo_tit_pagar,'S')	= 'S';
		else
			select	coalesce(sum(vl_imposto),0)
			into STRICT	vl_tributo_w
			from	titulo_pagar_imposto a,
					tributo b
			where	a.cd_tributo 	= b.cd_tributo
			and		nr_titulo		= nr_titulo_p
			and		ie_pago_prev	= 'V'
			and		coalesce(b.ie_saldo_tit_pagar,'S')	= 'S';
		end if;	
	end if;		
end if;
/*OS 1586772 FIM */



/* Edgar 12/01/2010, OS 188207, tirado este tratamento
select	nvl(sum(vl_adiantamento),0)
into	vl_adiantamento_w
from	titulo_pagar_adiant
where	nr_titulo	= nr_titulo_p;
*/
vl_base_classif_w	:= vl_base_classif_w - vl_tributo_w;

open c01;
loop
fetch c01 into
	nr_sequencia_w,
	vl_original_w,
	vl_classificacao_w,
	VL_DESCONTO_classif_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	if (vl_original_w = 0) then
		vl_original_w		:= vl_classificacao_w;
	end if;

	vl_classificacao_w		:= dividir_sem_round(vl_base_classif_w - vl_adiantamento_w, vl_titulo_w) * vl_original_w;
	/* Francisco - OS 60401 - Ao inves de vl_titulo_w estava vl_baixa_w, estava errado alterei */

	VL_DESCONTO_classif_w	:= vl_classificacao_w * dividir_sem_round(vl_descontos_w, vl_titulo_w);
	vl_acrescimo_classif_w	:= vl_classificacao_w * dividir_sem_round(vl_acrescimos_w, vl_titulo_w);

	update	titulo_pagar_classif
	set	vl_original	= vl_original_w,
		vl_titulo	= vl_classificacao_w,
		vl_desconto	= vl_DESCONTO_classif_w,
		nm_usuario	= coalesce(nm_usuario_p, nm_usuario),
		dt_atualizacao	= clock_timestamp(),
		vl_acrescimo	= vl_acrescimo_classif_w
	where	nr_titulo	= nr_titulo_p
	and	nr_sequencia	= nr_sequencia_w;

end loop;
close c01;

-- Edgar 25/05/2005, arredondamento vl_acrescimo
if (coalesce(nr_sequencia_w,0) > 0) then

	select	max(nr_sequencia)
	into STRICT	nr_sequencia_maior_valor_w
	from	titulo_pagar_classif
	where	nr_titulo	= nr_titulo_p
	and  	vl_acrescimo =  (SELECT max(x.vl_acrescimo) from titulo_pagar_classif x where x.nr_titulo = nr_titulo_p);
	
	select	sum(vl_acrescimo)
	into STRICT	vl_total_acrescimo_w
	from	titulo_pagar_classif
	where	nr_titulo	= nr_titulo_p;

	update	titulo_pagar_classif
	set	vl_acrescimo = vl_acrescimo + (vl_acrescimos_w - vl_total_acrescimo_w)
	where	nr_titulo	= nr_titulo_p
	and	nr_sequencia	= nr_sequencia_maior_valor_w;
	
end if;

-- Edgar 25/05/2005, arredondamento
if (coalesce(nr_sequencia_w,0) > 0) then

	select	sum(vl_titulo),
		sum(vl_desconto)
	into STRICT	vl_total_w,
		vl_total_desconto_w
	from	titulo_pagar_classif
	where	nr_titulo	= nr_titulo_p;
	
	update	titulo_pagar_classif
	set	vl_titulo	= vl_titulo + (vl_base_classif_w - vl_total_w),
		vl_desconto	= vl_desconto + (vl_descontos_w - vl_total_desconto_w)
	where	nr_titulo	= nr_titulo_p
	and	nr_sequencia	= nr_sequencia_w;
	
end if;

if (ie_commit_p = 'S') then
	if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE atualizar_tit_pagar_classif (nr_titulo_p bigint, ie_commit_p text, nm_usuario_p text) FROM PUBLIC;

