SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_total_horas_cron (nr_seq_cronograma_p bigint) AS $body$
DECLARE


qt_hora_prev_w	double precision;
qt_hora_real_w	double precision;
qt_hora_saldo_w	double precision;


BEGIN
if (nr_seq_cronograma_p IS NOT NULL AND nr_seq_cronograma_p::text <> '') then
	select	coalesce(sum(coalesce(a.qt_hora_prev,0)),0),
		coalesce(sum(coalesce(a.qt_hora_real,0)),0)
	into STRICT	qt_hora_prev_w,
		qt_hora_real_w
	from	proj_cron_etapa a
	where	a.nr_seq_cronograma	= nr_seq_cronograma_p
	and	coalesce(nr_seq_superior::text, '') = ''
	and	coalesce(a.ie_situacao,'A') = 'A';

	update	proj_cronograma
	set	qt_total_horas 		= qt_hora_prev_w,
		qt_horas_realizado 	= qt_hora_real_w,
		qt_horas_saldo		= (qt_hora_prev_w - qt_hora_real_w),
		pr_previsao		= dividir((qt_hora_real_w * 100), qt_hora_prev_w)
	where	nr_sequencia		= nr_seq_cronograma_p;
end if;

COMMIT;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
