CREATE OR REPLACE PROCEDURE atualizar_triagem_classif_sv ( nr_seq_item_examinar_p bigint, nr_seq_triagem_p bigint, qt_pa_diastolica_p bigint, cd_escala_dor_p text, qt_escala_dor_p bigint, qt_temp_p bigint, qt_freq_cardiaca_p bigint, qt_freq_resp_p bigint, qt_glicemia_capilar_p bigint, qt_pa_sistolica_p bigint, qt_saturacao_o2_p bigint) AS $body$
DECLARE

		
nr_seq_triagem_classif_sv_w		triagem_classificatoria_sv.nr_sequencia%type;
		

BEGIN
if (nr_seq_item_examinar_p IS NOT NULL AND nr_seq_item_examinar_p::text <> '') and (nr_seq_triagem_p IS NOT NULL AND nr_seq_triagem_p::text <> '') then
	begin
		
	select	max(nr_sequencia)
	into STRICT	nr_seq_triagem_classif_sv_w
	from	triagem_classificatoria_sv
	where	nr_seq_item_examinar = nr_seq_item_examinar_p
	and		nr_seq_triagem = nr_seq_triagem_p;

	if (nr_seq_triagem_classif_sv_w IS NOT NULL AND nr_seq_triagem_classif_sv_w::text <> '') then
		
		update	triagem_classificatoria_sv
		set		dt_atualizacao		= clock_timestamp(),
				nm_usuario			= obter_usuario_ativo,
				cd_escala_dor		= cd_escala_dor_p,
				qt_escala_dor		= qt_escala_dor_p,
				qt_temp				= qt_temp_p,
				qt_freq_cardiaca	= qt_freq_cardiaca_p,
				qt_freq_resp		= qt_freq_resp_p,
				qt_glicemia_capilar	= qt_glicemia_capilar_p,
				qt_pa_sistolica		= qt_pa_sistolica_p,
				qt_pa_diastolica	= qt_pa_diastolica_p,
				qt_saturacao_o2		= qt_saturacao_o2_p
		where	nr_sequencia = nr_seq_triagem_classif_sv_w;
		
	else
	
		insert into triagem_classificatoria_sv(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_triagem,
			nr_seq_item_examinar,
			cd_escala_dor,
			qt_escala_dor,
			qt_temp,
			qt_freq_cardiaca,
			qt_freq_resp,
			qt_glicemia_capilar,
			qt_pa_sistolica,
			qt_pa_diastolica,
			qt_saturacao_o2)
		values (
			nextval('triagem_classificatoria_sv_seq'),
			clock_timestamp(),
			obter_usuario_ativo,
			clock_timestamp(),
			obter_usuario_ativo,
			nr_seq_triagem_p,
			nr_seq_item_examinar_p,
			cd_escala_dor_p,
			qt_escala_dor_p,
			qt_temp_p,
			qt_freq_cardiaca_p,
			qt_freq_resp_p,
			qt_glicemia_capilar_p,
			qt_pa_sistolica_p,
			qt_pa_diastolica_p,
			qt_saturacao_o2_p);		
	end if;

	commit;
	end;

end if;
end;
$body$
