SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_trib_bordero_pag ( nr_bordero_p bigint, nm_usuario_p text, ie_opcao_p text, ie_titulo_bordero_p text, ie_ajusta_baixa_p text default 'N') AS $body$
DECLARE

 
nr_titulo_w		bigint;
vl_tributo_w		double precision;
vl_saldo_titulo_w	double precision;

c01 CURSOR FOR 
SELECT	 b.nr_titulo, 
		coalesce(obter_saldo_titulo_pagar( b.nr_titulo,clock_timestamp()),0) 
from	bordero_pagamento a, 
		titulo_pagar_bordero_v b 
where	b.nr_bordero	= a.nr_bordero 
and 	a.nr_bordero 	= nr_bordero_p;


BEGIN 
 
if (coalesce(nr_bordero_p,0) > 0) then 
 
	if (ie_ajusta_baixa_p = 'S' and ie_titulo_bordero_p = 'S') then 
 
		open c01;
		loop 
		fetch c01 into 
			nr_titulo_w, 
			vl_saldo_titulo_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
		 
			select	coalesce(sum(vl_imposto),0) 
			into STRICT	vl_tributo_w 
			from	w_titulo_pagar_imposto 
			where	nr_titulo	= nr_titulo_w;
 
			if (ie_opcao_p	= 'I') then 
			 
				update	bordero_tit_pagar 
				set		vl_bordero	= coalesce(vl_bordero,0) - coalesce(vl_tributo_w,0)	/* ahoffelder - 17/04/2013 - alterei pq os títulos nem sempre vão para o pagto escritural com o saldo inteiro */
 
				where	nr_bordero 	= nr_bordero_p 
				and	 	nr_titulo	= nr_titulo_w;
			else			 
				update	bordero_tit_pagar 
				set	vl_bordero	= coalesce(vl_bordero,0) + coalesce(vl_tributo_w,0) 
				where	nr_bordero = nr_bordero_p 
				and	 	nr_titulo	= nr_titulo_w;
				 
			end if;
		end loop;
		close c01;
	end if;
 
	if (ie_opcao_p	= 'E') then 
 
		delete	from w_titulo_pagar_imposto 
		where	nr_bordero	= nr_bordero_p;
 
		update	bordero_pagamento 
		set	dt_geracao_tributo	 = NULL, 
			nm_usuario		= nm_usuario_p, 
			dt_atualizacao		= clock_timestamp() 
		where	nr_bordero		= nr_bordero_p;
	else 
		update	bordero_pagamento 
		set	dt_geracao_tributo	= clock_timestamp(), 
			nm_usuario		= nm_usuario_p, 
			dt_atualizacao		= clock_timestamp() 
		where	nr_bordero		= nr_bordero_p;
 
	end if;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE atualizar_trib_bordero_pag ( nr_bordero_p bigint, nm_usuario_p text, ie_opcao_p text, ie_titulo_bordero_p text, ie_ajusta_baixa_p text default 'N') FROM PUBLIC;

