SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_tx_espec_tec_mat ( nm_usuario_p text, nr_sequencia_p bigint) AS $body$
DECLARE


nr_seq_rtf_srtring_w	varchar(50);
ds_espec_w		varchar(4000);


BEGIN


	nr_seq_rtf_srtring_w := converte_rtf_string('select ds_espec_tecnica
			     from  material_espec_tecnica
			     where nr_sequencia = :nr_sequencia_p', nr_sequencia_p, nm_usuario_p, nr_seq_rtf_srtring_w);

	begin

		select	ds_texto
		into STRICT	ds_espec_w
		from	tasy_conversao_rtf
		where	nr_sequencia = nr_seq_rtf_srtring_w;

	exception
	when others then
		ds_espec_w := null;
	end;

	if (coalesce(ds_espec_w::text, '') = '')then
		ds_espec_w := convert_long_html_to_str('ds_espec_tecnica', 'material_espec_tecnica', 'nr_sequencia = ' ||  nr_sequencia_p);
	end if;

	if (ds_espec_w IS NOT NULL AND ds_espec_w::text <> '') then

		update 	material_espec_tecnica
		set 	ds_espec_tecnica_tx = ds_espec_w
		where 	nr_sequencia = nr_sequencia_p;

		commit;

	end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
