CREATE OR REPLACE PROCEDURE atualizar_ult_acesso_os ( nr_seq_ordem_p bigint, ie_operacao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_grupo_des_os_w	bigint;
nm_usuario_lider_w		varchar(15);
nm_user_w		varchar(50);
qt_existe_w		bigint;


BEGIN

CALL man_atualizar_ult_visao(nr_seq_ordem_p,nm_usuario_p,ie_operacao_p);

select	username
into STRICT	nm_user_w
from	user_users;

if (nm_user_w = 'CORP') then
	begin
	select	max(nr_seq_grupo_des)
	into STRICT	nr_seq_grupo_des_os_w
	from	man_ordem_servico
	where	nr_sequencia = nr_seq_ordem_p;

	select	count(*)
	into STRICT	qt_existe_w
	from	usuario_grupo_des
	where	ie_funcao_usuario	= 'S'
	and	nm_usuario_grupo	= nm_usuario_p
	and	nr_seq_grupo	= nr_seq_grupo_des_os_w;

	if (qt_existe_w > 0) then
		update	man_ordem_servico
		set	dt_ult_acesso_lider	= clock_timestamp()
		where	nr_sequencia	= nr_seq_ordem_p;

		commit;
	end if;

	exception
	when others then
	null;
	end;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
