SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_undidade_eup ( cd_unidade_basica_p text, cd_unidade_compl_p text, cd_setor_atendimento_p bigint, nr_atendimento_p bigint, dt_entrada_unidade_p timestamp, nm_usuario_p text) AS $body$
BEGIN
 
if (dt_entrada_unidade_p IS NOT NULL AND dt_entrada_unidade_p::text <> '') and (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	begin 
	 
	update	unidade_atendimento a 
	set 	a.nr_atendimento   	= nr_atendimento_p, 
		a.dt_entrada_unidade 	= dt_entrada_unidade_p, 
		a.ie_status_unidade  	= 'P', 
		a.cd_paciente_reserva 	 = NULL, 
		a.nm_pac_reserva		 = NULL, 
		a.cd_convenio_reserva	 = NULL, 
		a.nm_usuario		= nm_usuario_p, 
		a.dt_atualizacao		= clock_timestamp() 
	where 	a.cd_unidade_basica 	= cd_unidade_basica_p 
	and 	a.cd_unidade_compl 	= cd_unidade_compl_p 
	and 	a.cd_setor_atendimento 	= cd_setor_atendimento_p 
	and 	a.cd_setor_atendimento in (SELECT cd_setor_atendimento 
	    from setor_atendimento 
	    where cd_classif_setor in (3,4));
	    
	CALL atualizar_unidade_atendimento(cd_setor_atendimento_p, 
					cd_unidade_basica_p, 
					cd_unidade_compl_p);
	end;
end if;
					 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
