CREATE OR REPLACE PROCEDURE atualizar_unid_atend_sit_eup ( nr_seq_interno_p bigint, ie_situacao_p text, nm_usuario_p text, cd_estabelecimento_p bigint, ds_erro_p INOUT text, ie_verifica_clique_p text default 'N') AS $body$
DECLARE


ie_atualiza_agrupamento_w	varchar(1);
nr_agrupamento_w		integer;
ds_erro_w			varchar(255);
cd_setor_atendimento_w		integer;
cd_unidade_basica_w		varchar(10);
cd_unidade_compl_w		varchar(10);



BEGIN

ds_erro_w:='';

ie_atualiza_agrupamento_w := Obter_Param_Usuario(1, 6, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_atualiza_agrupamento_w);

select 	coalesce(max(nr_agrupamento),0),
	max(cd_setor_atendimento),
	max(cd_unidade_basica),
	max(cd_unidade_compl)
into STRICT	nr_agrupamento_w,
	cd_setor_atendimento_w,
	cd_unidade_basica_w,
	cd_unidade_compl_w
from	unidade_atendimento
where	nr_seq_interno 	= nr_seq_interno_p;

if (nr_agrupamento_w > 0) and (ie_atualiza_agrupamento_w = 'S') then

	 ds_erro_w := atualizar_situacao_leito(cd_setor_atendimento_w, cd_unidade_basica_w, cd_unidade_compl_w, ie_situacao_p, nm_usuario_p, ds_erro_w, ie_verifica_clique_p);

else
	update	unidade_atendimento
	set	ie_situacao	= ie_situacao_p,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_seq_interno 	= nr_seq_interno_p;
end if;

ds_erro_p	:= ds_erro_w;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
