CREATE OR REPLACE PROCEDURE atualizar_unid_dialise ( cd_pessoa_fisica_p text, nr_seq_unid_dialise_p bigint) AS $body$
BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	update 	hd_pac_renal_cronico
	set	NR_SEQ_UNIDADE_ATUAL 	= CASE WHEN coalesce(nr_seq_unid_dialise_p,0)=0 THEN  null  ELSE nr_seq_unid_dialise_p END
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p;

end if;

commit;

end;
$body$
