CREATE OR REPLACE PROCEDURE atualizar_unid_med_agente ( nr_sequencia_p bigint, ie_modo_adm_p text, cd_unid_medida_p text, nm_usuario_p text) AS $body$
BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (cd_unid_medida_p IS NOT NULL AND cd_unid_medida_p::text <> '') and (ie_modo_adm_p = 'C') then

	update	cirurgia_agente_anestesico
	set	nm_usuario		=	nm_usuario_p,
		dt_atualizacao		=	clock_timestamp(),
		ie_tipo_dosagem		=	cd_unid_medida_p,
		cd_unid_medida_adm	 = NULL
	where	nr_sequencia		=	nr_sequencia_p;

elsif (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (cd_unid_medida_p IS NOT NULL AND cd_unid_medida_p::text <> '') and (ie_modo_adm_p = 'I') then

	update	cirurgia_agente_anestesico
	set	nm_usuario		=	nm_usuario_p,
		dt_atualizacao		=	clock_timestamp(),
		ie_tipo_dosagem		 = NULL,
		cd_unid_medida_adm	=	cd_unid_medida_p
	where	nr_sequencia		=	nr_sequencia_p;

end if;

commit;

end;
