CREATE OR REPLACE PROCEDURE atualizar_unid_med_convenio ( cd_convenio_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_unidade_medida_w	unidade_medida.cd_unidade_medida%type;

c01 CURSOR FOR
	SELECT	cd_unidade_medida
	from	unidade_medida
	where	ie_situacao = 'A';


BEGIN

open 	c01;
loop
fetch c01 	into
		cd_unidade_medida_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		if (length(cd_unidade_medida_w) <= 10) then

			insert into conversao_unidade_medida(
					cd_convenio,
					cd_unidade_medida,
					cd_unidade_convenio,
					dt_atualizacao,
					nm_usuario
					)
			values (
					cd_convenio_p,
					cd_unidade_medida_w,
					cd_unidade_medida_w,
					clock_timestamp(),
					nm_usuario_p
				);
		end if;

		end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
