CREATE OR REPLACE PROCEDURE atualizar_usuario_setor ( cd_lista_setor_p text, nm_usuario_param_P text, nm_usuario_p text) AS $body$
DECLARE



nr_seq_lista_w		varchar(255);
nr_pos_virgula_w	bigint;
cd_setor_atendimento_w	integer;


BEGIN
if (cd_lista_setor_p IS NOT NULL AND cd_lista_setor_p::text <> '')	then
	begin
	nr_seq_lista_w	:= cd_lista_setor_p;
	while(nr_seq_lista_w IS NOT NULL AND nr_seq_lista_w::text <> '') loop
		begin
		nr_pos_virgula_w	:= position(',' in nr_seq_lista_w);
		if (nr_pos_virgula_w > 0)	then
			begin
			cd_setor_atendimento_w	:= (substr(nr_seq_lista_w,1,nr_pos_virgula_w-1))::numeric;
			nr_seq_lista_w	:= substr(nr_seq_lista_w,nr_pos_virgula_w+1,length(nr_seq_lista_w));
			if (cd_setor_atendimento_w > 0) then
				begin
				insert 	into usuario_setor(
							nm_usuario_param,
							cd_setor_atendimento,
							dt_atualizacao,
							nm_usuario)
						values (nm_usuario_param_P,
							cd_setor_atendimento_w,
							clock_timestamp(),
							nm_usuario_p);
				end;
			end if;
			end;
		end if;
		end;
	end loop;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
