SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_utilizacao_senha (nr_seq_senha_p bigint) AS $body$
DECLARE

 
nm_usuario_chamada_w 		varchar(20);
nm_usuario_ativo_w		varchar(20);
ie_consiste_usuario_w		varchar(2);

BEGIN
nm_usuario_ativo_w := obter_usuario_ativo;
 
select 	max(nm_usuario_chamada) 
into STRICT	nm_usuario_chamada_w 
from	paciente_senha_fila 
where	(dt_primeira_chamada IS NOT NULL AND dt_primeira_chamada::text <> '') 
and	nr_sequencia = nr_seq_senha_p;
 
ie_consiste_usuario_w := Obter_Param_Usuario(10021, 61, Obter_perfil_Ativo, nm_usuario_ativo_w, obter_estabelecimento_ativo, ie_consiste_usuario_w);
 
if 	((ie_consiste_usuario_w = 'S') or 
	((ie_consiste_usuario_w = 'M') and (obter_se_usuario_medico(nm_usuario_chamada_w) = 'S') and (obter_se_usuario_medico(nm_usuario_ativo_w) = 'S'))) and (coalesce(nm_usuario_chamada_w::text, '') = '') and (nm_usuario_chamada_w <> nm_usuario_ativo_w) then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(219253, 'NM_USUARIO_CHAMADA=' || nm_usuario_chamada_w);
end if;
 
if (coalesce(nr_seq_senha_p,0) > 0) then 
 
	update	paciente_senha_fila 
	set	dt_utilizacao = clock_timestamp() 
	where	nr_sequencia = nr_seq_senha_p;
 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
