SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_valores_neg_cr ( nr_seq_negociacao_p bigint, nm_usuario_p text, ie_commit_p text) AS $body$
DECLARE


vl_total_negociado_w	double precision;
vl_saldo_negociacao_w	double precision;
vl_juros_w		double precision;
vl_multa_w		double precision;
vl_desconto_w		double precision;
vl_boleto_w		double precision;
vl_debito_cc_w		double precision;
pr_taxa_w		double precision;
vl_taxa_w		double precision;
vl_mensalidade_w	double precision;
vl_juros_tit_w		double precision;
vl_multa_tit_w		double precision;
vl_total_taxa_w		double precision;
nr_seq_regra_result_w	bigint;
cd_estabelecimento_w	smallint;
dt_negociacao_w		timestamp;

C01 CURSOR FOR
	SELECT	a.pr_taxa,
		a.vl_taxa
	from	regra_taxa_negociacao_cr a
	where	a.cd_estabelecimento	= cd_estabelecimento_w
	and	dt_negociacao_w between trunc(a.dt_inicio_vigencia,'dd') and fim_dia(coalesce(a.dt_fim_vigencia,to_date('31/12/2100','dd/mm/yyyy')))
	order by
		a.dt_inicio_vigencia;


BEGIN

if (nr_seq_negociacao_p IS NOT NULL AND nr_seq_negociacao_p::text <> '') then
	select	vl_juros,
		vl_multa,
		vl_desconto,
		dt_negociacao,
		tx_negociacao,
		cd_estabelecimento,
		nr_seq_regra_resultado
	into STRICT	vl_juros_w,
		vl_multa_w,
		vl_desconto_w,
		dt_negociacao_w,
		vl_taxa_w,
		cd_estabelecimento_w,
		nr_seq_regra_result_w
	from	negociacao_cr a
	where	nr_sequencia	= nr_seq_negociacao_p;

	select	coalesce(sum(a.vl_negociado),0),
		coalesce(sum(a.vl_juros),0),
		coalesce(sum(a.vl_multa),0)
	into STRICT	vl_total_negociado_w,
		vl_juros_tit_w,
		vl_multa_tit_w
	from	titulo_rec_negociado a
	where	a.nr_seq_negociacao	= nr_seq_negociacao_p;

	vl_total_taxa_w	:= vl_total_negociado_w + vl_juros_w + vl_multa_w - vl_desconto_w;

	select	coalesce(sum(a.vl_parcela),0)
	into STRICT	vl_boleto_w
	from	negociacao_cr_boleto a
	where	a.nr_seq_negociacao	= nr_seq_negociacao_p
	and	(a.nr_titulo IS NOT NULL AND a.nr_titulo::text <> '');

	select	coalesce(sum(a.vl_debito),0)
	into STRICT	vl_debito_cc_w
	from	negociacao_cr_deb_cc a
	where	a.nr_seq_negociacao	= nr_seq_negociacao_p
	and	(a.nr_titulo IS NOT NULL AND a.nr_titulo::text <> '');

	select	coalesce(sum(a.vl_mensalidade),0)
	into STRICT	vl_mensalidade_w
	from	negociacao_cr_pls_mens a
	where	a.nr_seq_negociacao	= nr_seq_negociacao_p
	and	exists (SELECT	1
			from	pls_lancamento_mensalidade x
			where	x.nr_seq_negociacao_mens = a.nr_sequencia);


	if (nr_seq_regra_result_w IS NOT NULL AND nr_seq_regra_result_w::text <> '') then
		select	coalesce(max(tx_juros),0)
		into STRICT	pr_taxa_w
		from	regra_resultado_neg_cr
		where	nr_sequencia	= nr_seq_regra_result_w;

		if (pr_taxa_w > 0) then
			vl_taxa_w	:=	vl_total_taxa_w * (pr_taxa_w / 100);
		else
			vl_taxa_w	:= vl_taxa_w;
		end if;
	else
		open C01;
		loop
		fetch C01 into
			pr_taxa_w,
			vl_taxa_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			if (pr_taxa_w > 0) then
				vl_taxa_w	:=	vl_total_taxa_w * (pr_taxa_w / 100);
			else
				vl_taxa_w	:= vl_taxa_w;
			end if;
		end loop;
		close C01;
	end if;

	vl_saldo_negociacao_w	:= vl_total_negociado_w + vl_juros_w + vl_multa_w - vl_desconto_w +
					vl_taxa_w - vl_boleto_w - vl_debito_cc_w - vl_mensalidade_w;

	update	negociacao_cr
	set	vl_negociado	= vl_total_negociado_w,
		vl_juros	= vl_juros_tit_w,
		vl_multa	= vl_multa_tit_w,
		tx_negociacao	= vl_taxa_w
	where	nr_sequencia	= nr_seq_negociacao_p;
end if;

if (ie_commit_p = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE atualizar_valores_neg_cr ( nr_seq_negociacao_p bigint, nm_usuario_p text, ie_commit_p text) FROM PUBLIC;

