CREATE OR REPLACE PROCEDURE atualizar_valores_tit_rec_neg ( nr_seq_negociacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_titulo_w		bigint;
vl_juros_w		double precision;
vl_multa_w		double precision;
dt_prev_pagto_w		timestamp;

c01 CURSOR FOR
SELECT	nr_titulo
from	titulo_rec_negociado
where	nr_seq_negociacao = nr_seq_negociacao_p;


BEGIN

if (nr_seq_negociacao_p IS NOT NULL AND nr_seq_negociacao_p::text <> '') then

	select	a.dt_prev_pagto
	into STRICT	dt_prev_pagto_w
	from	negociacao_cr a
	where	a.nr_sequencia	= nr_seq_negociacao_p;

	open c01;
	loop
	fetch c01 into
		nr_titulo_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
			vl_juros_w	:= obter_juros_multa_titulo(nr_titulo_w,coalesce(dt_prev_pagto_w, clock_timestamp()),'R','J');
			vl_multa_w	:= obter_juros_multa_titulo(nr_titulo_w,coalesce(dt_prev_pagto_w, clock_timestamp()),'R','M');

			update	titulo_rec_negociado
			set	vl_juros = vl_juros_w,
				vl_multa = vl_multa_w
			where	nr_titulo = nr_titulo_w;
		end;
	end loop;
	close c01;

	CALL atualizar_valores_neg_cr(nr_seq_negociacao_p,nm_usuario_p,'N');
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
