SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_valor_desp_viagem ( nr_sequencia_p bigint, nr_seq_relat_desp_p bigint, vl_pago_terceiro_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_total_desp_w			double precision;
ie_pago_terceiro_w		via_relat_desp.ie_pago_terceiro%type;
nr_seq_viagem_w			via_relat_desp.nr_seq_viagem%type;
ds_retorno_w			varchar(255);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	/*Atualizar  se foi pagou ou nao por terceiro*/

	update via_relat_desp
    set    ie_pago_terceiro 	= CASE WHEN coalesce(ie_pago_terceiro, 'N')='N' THEN  'S'  ELSE 'N' END
    where  nr_sequencia 		= nr_seq_relat_desp_p
    and    nr_seq_fech_proj 	=  nr_sequencia_p;

	 /*Pegar a situacao atualizada da GV*/

	select	max(ie_pago_terceiro)
	into STRICT	ie_pago_terceiro_w
	from	via_relat_desp
	where  nr_sequencia 		= nr_seq_relat_desp_p
    and    nr_seq_fech_proj 	=  nr_sequencia_p;

	if (ie_pago_terceiro_w = 'S') then

		/*Buscar o valor total das despesas dessa viagem para deduzir a que esta sendo paga pelo terceiro*/

		select  coalesce(sum(obter_valor_desp_viagem(a.nr_sequencia,'4')),0)
		into STRICT	vl_total_desp_w
		from    via_relat_desp a
		where   a.nr_seq_fech_proj = nr_sequencia_p
		and		a.nr_sequencia <> nr_sequencia_p
		and		coalesce(a.ie_pago_terceiro,'N') <> 'S';

		update 	proj_desp_viagem_mes
		set		vl_total_pagar 	= vl_total_desp_w,
				nm_usuario 		= nm_usuario_p,
				dt_atualizacao	= clock_timestamp()
		where	nr_sequencia	= nr_sequencia_p;

		select	max(a.nr_seq_viagem)
		into STRICT	nr_seq_viagem_w
		from	via_relat_desp a
		where	a.nr_sequencia	=	nr_seq_relat_desp_p;

		if (nr_seq_viagem_w IS NOT NULL AND nr_seq_viagem_w::text <> '') then

			ds_retorno_w := null;

			ds_retorno_w := gerenciar_viagem(nr_seq_viagem_w, nm_usuario_p, 'F', ds_retorno_w, 'N'); --Não para o commit. Pois commit ocorre no final dessa proc aqui..
			if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
				CALL wheb_mensagem_pck.exibir_mensagem_abort(1020093,'ds_retorno_p='||ds_retorno_w);
			end if;

		end if;

	elsif (ie_pago_terceiro_w = 'N') then

		select  coalesce(sum(obter_valor_desp_viagem(a.nr_sequencia,'4')),0)
		into STRICT	vl_total_desp_w
		from    via_relat_desp a
		where   a.nr_seq_fech_proj = nr_sequencia_p
		and		a.nr_sequencia <> nr_seq_relat_desp_p
		and		coalesce(a.ie_pago_terceiro,'N') = 'N';

		update 	proj_desp_viagem_mes
		set		vl_total_pagar 	= coalesce(vl_total_desp_w,0) + coalesce(vl_pago_terceiro_p,0),
				nm_usuario 		= nm_usuario_p,
				dt_atualizacao	= clock_timestamp()
		where	nr_sequencia	= nr_sequencia_p;

		/*OS 1670837 - Desfazer finalização da viagem quando desfazer pago terceiro.*/

		select	max(a.nr_seq_viagem)
		into STRICT	nr_seq_viagem_w
		from	via_relat_desp a
		where	a.nr_sequencia	=	nr_seq_relat_desp_p;

		if (nr_seq_viagem_w IS NOT NULL AND nr_seq_viagem_w::text <> '') then

				update	via_viagem
				set		ie_etapa_viagem			= '5',
						dt_fim_viagem			 = NULL,
						nm_usuario_fim_viagem	 = NULL,
						dt_atualizacao			= clock_timestamp()
				where	nr_sequencia			= nr_seq_viagem_w;

		end if;

	end if;

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE atualizar_valor_desp_viagem ( nr_sequencia_p bigint, nr_seq_relat_desp_p bigint, vl_pago_terceiro_p bigint, nm_usuario_p text) FROM PUBLIC;

