CREATE OR REPLACE PROCEDURE atualizar_valor_guia_ret (nr_seq_retorno_p bigint) AS $body$
DECLARE


vl_pago_dig_w		convenio_retorno_glosa.vl_pago_digitado%type;
vl_glosa_w			convenio_retorno_glosa.vl_glosa%type;
vl_amenor_w			convenio_retorno_glosa.vl_glosa%type;
nr_seq_ret_item_w	convenio_retorno_glosa.nr_seq_ret_item%type;

C01 CURSOR FOR
SELECT	nr_seq_ret_item
from	convenio_retorno_glosa
where	nr_seq_retorno = nr_seq_retorno_p
group by nr_seq_ret_item
order by 1;


BEGIN

open C01;
loop
fetch C01 into
		nr_seq_ret_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	select	coalesce(sum(a.vl_glosa),0)
	into STRICT	vl_glosa_w
	from	convenio_retorno_glosa a,
			motivo_glosa b
	where	nr_seq_retorno 		= nr_seq_retorno_p
	and		a.cd_motivo_glosa 	= b.cd_motivo_glosa
	and		nr_seq_ret_item 	= nr_seq_ret_item_w
	and		b.ie_acao_glosa 	= 'A';

	select	coalesce(sum(vl_pago_digitado),0)
	into STRICT	vl_pago_dig_w
	from	convenio_retorno_glosa
	where	nr_seq_retorno = nr_seq_retorno_p
	and		nr_seq_ret_item = nr_seq_ret_item_w;

	select	coalesce(sum(a.vl_glosa),0)
	into STRICT	vl_amenor_w
	from	convenio_retorno_glosa a,
			motivo_glosa b
	where	a.cd_motivo_glosa 	= b.cd_motivo_glosa
	and		nr_seq_retorno 		= nr_seq_retorno_p
	and		nr_seq_ret_item 	= nr_seq_ret_item_w
	and		b.ie_acao_glosa 	= 'R';
	update	convenio_retorno_item
	set		vl_pago			= vl_pago_dig_w,
			vl_amenor		= vl_amenor_w,
			vl_glosado		= vl_glosa_w
	where	nr_seq_retorno 	= nr_seq_retorno_p
	and		nr_sequencia 	= nr_seq_ret_item_w;
end loop;

close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
