CREATE OR REPLACE PROCEDURE atualizar_valor_item_analise (cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_lote_hist_p bigint, cd_motivo_glosa_novo_p bigint, nr_seq_hist_guia_p bigint default null) AS $body$
DECLARE


nr_sequencia_w		bigint;
ie_acao_glosa_w		varchar(1);

c01 CURSOR FOR
SELECT	a.nr_sequencia,
	a.ie_acao_glosa
from	lote_audit_hist_item a
where	a.cd_motivo_glosa	= cd_motivo_glosa_novo_p
and	a.nr_sequencia    in (select  y.nr_sequencia
				from    lote_audit_hist_item y,
					lote_audit_hist_guia x
                               	where	x.nr_seq_lote_hist   = nr_seq_lote_hist_p
                                and     x.nr_sequencia       = y.nr_seq_guia);

BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	ie_acao_glosa_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ie_acao_glosa_w = 'A') then

		update	lote_audit_hist_item
		set	vl_glosa	= vl_glosa_informada,
			vl_amenor	= 0,
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_sequencia_w
		and	((coalesce(nr_seq_hist_guia_p,0) = 0) or (nr_seq_hist_guia_p = nr_sequencia));

	elsif (ie_acao_glosa_w = 'R') then

		update	lote_audit_hist_item
		set	vl_glosa	= 0,
			vl_amenor	= vl_glosa_informada,
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_sequencia_w
		and	((coalesce(nr_seq_hist_guia_p,0) = 0) or (nr_seq_hist_guia_p = nr_sequencia));
	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
