CREATE OR REPLACE PROCEDURE atualizar_valor_item_dev (nr_seq_nota_p bigint, nr_item_nf_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_unitario_w		nota_fiscal_item.vl_unitario_item_nf%type;
vl_total_item_w		nota_fiscal_item.vl_total_item_nf%type;
vl_liquido_item_w	nota_fiscal_item.vl_liquido%type;
vl_descontos_w          nota_fiscal_item.vl_desconto%type;
qt_item_nf_w	        nota_fiscal_item.qt_item_nf%type;
qt_item_estoque_w       nota_fiscal_item.qt_item_estoque%type;


BEGIN

select	coalesce(max(a.vl_unitario_item_nf), 0),
	coalesce(max(a.vl_desconto), 0),
	coalesce(sum(b.qt_compra),0),
	coalesce(sum(b.qt_material),0)
into STRICT	vl_unitario_w,
	vl_descontos_w,
	qt_item_nf_w,
	qt_item_estoque_w
from 	nota_fiscal_item a, nota_fiscal_item_lote b
where	a.nr_sequencia = nr_seq_nota_p
and	a.nr_item_nf = nr_item_nf_p
and	b.nr_seq_nota = a.nr_sequencia
and	b.nr_item_nf = a.nr_item_nf;

vl_total_item_w := qt_item_nf_w * vl_unitario_w;
vl_liquido_item_w := vl_total_item_w - vl_descontos_w;

update	nota_fiscal_item
set	vl_total_item_nf = vl_total_item_w,
	vl_liquido = vl_liquido_item_w,
	qt_item_nf = qt_item_nf_w,
	qt_item_estoque = qt_item_estoque_w
where	nr_sequencia = nr_seq_nota_p
and	nr_item_nf = nr_item_nf_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
