SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_valor_lote_recurso (nr_seq_lote_p bigint, ie_acao_p text, nm_usuario_p text) AS $body$
DECLARE


/*
 ie_acao_p
        'F' - Fechar análise
        'A' - Reabrir liberação

*/
vl_amenor_w	double precision;
vl_pago_w	double precision;
vl_glosado_w	double precision;


BEGIN

if (ie_acao_p = 'F') then

	select	coalesce(sum(a.vl_amenor),0),
		coalesce(sum(a.vl_pago),0),
		coalesce(sum(a.vl_glosa),0)
	into STRICT	vl_amenor_w,
		vl_pago_w,
		vl_glosado_w
	from	lote_audit_hist_item a,
		lote_audit_hist_guia b
	where	b.nr_seq_lote_hist	= nr_seq_lote_p
	and	b.nr_sequencia		= a.nr_seq_guia;


	update	lote_audit_hist
	set 	vl_recurso	=	vl_amenor_w,
		vl_pago		=	vl_pago_w,
		vl_glosa	= 	vl_glosado_w,
		nm_usuario 	=	nm_usuario_p
	where 	nr_sequencia	=	nr_seq_lote_p;

elsif (ie_acao_p = 'A') then

	update	lote_audit_hist
	set 	vl_recurso	=	0,
		vl_pago		= 	0,
		vl_glosa	=	0,
		nm_usuario 	=	nm_usuario_p
	where 	nr_sequencia	=	nr_seq_lote_p;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
